import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Row, 
  Col, 
  ProgressBar,
  Button,
  Dropdown,
  Alert
} from 'react-bootstrap';
import { 
  CurrencyDollar, 
  GraphUp, 
  PiggyBank,
  Bell,
  ThreeDots
} from 'react-bootstrap-icons';
import { Doughnut, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [savingsGoal, setSavingsGoal] = useState(1000);
  const [currentSavings, setCurrentSavings] = useState(450);
  const [notifications, setNotifications] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        // Fetch user data
        const userResponse = await axios.get('http://localhost:8000/api/user/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserData(userResponse.data);

        // Fetch transactions
        const transactionsResponse = await axios.get('http://localhost:8000/api/transactions/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTransactions(transactionsResponse.data);

        // Fetch notifications
        const notificationsResponse = await axios.get('http://localhost:8000/api/notifications/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setNotifications(notificationsResponse.data);

      } catch (error) {
        console.error('Error fetching data:', error);
        localStorage.removeItem('access_token');
        navigate('/login');
      }
    };

    fetchData();
  }, [navigate]);

  // Calculate financial data
  const income = transactions
    .filter(t => t.type === 'income')
    .reduce((sum, t) => sum + parseFloat(t.amount), 0);

  const expenses = transactions
    .filter(t => t.type === 'expense')
    .reduce((sum, t) => sum + parseFloat(t.amount), 0);

  const savings = income - expenses;
  const savingsProgress = (currentSavings / savingsGoal) * 100;

  // Chart data
  const doughnutData = {
    labels: ['Income', 'Expenses'],
    datasets: [{
      data: [income, expenses],
      backgroundColor: ['#36A2EB', '#FF6384'],
      hoverBackgroundColor: ['#36A2EB', '#FF6384']
    }]
  };

  const barData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Income',
        data: [500, 600, 700, 800, 900, 1000],
        backgroundColor: '#36A2EB'
      },
      {
        label: 'Expenses',
        data: [300, 400, 500, 600, 700, 800],
        backgroundColor: '#FF6384'
      }
    ]
  };

  const barOptions = {
    responsive: true,
    scales: {
      x: { type: 'category' },
      y: { type: 'linear', beginAtZero: true }
    }
  };

  return (
    <div className="dashboard-container">
      <Row className="mb-4">
        <Col>
          <h2>Dashboard Overview</h2>
          <p className="text-muted">Welcome back, {userData?.username || 'User'}!</p>
        </Col>
      </Row>

      {/* Financial Overview Cards */}
      <Row className="mb-4">
        <Col md={4}>
          <Card className="h-100">
            <Card.Body>
              <div className="d-flex justify-content-between align-items-center mb-3">
                <Card.Title className="mb-0">
                  <CurrencyDollar className="me-2" />
                  Income
                </Card.Title>
                <span className="badge bg-success">+12%</span>
              </div>
              <h3>${income.toFixed(2)}</h3>
              <p className="text-muted mb-0">This month</p>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="h-100">
            <Card.Body>
              <div className="d-flex justify-content-between align-items-center mb-3">
                <Card.Title className="mb-0">
                  <GraphUp className="me-2" />
                  Expenses
                </Card.Title>
                <span className="badge bg-danger">-5%</span>
              </div>
              <h3>${expenses.toFixed(2)}</h3>
              <p className="text-muted mb-0">This month</p>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="h-100">
            <Card.Body>
              <Card.Title>
                <PiggyBank className="me-2" />
                Savings Goal
              </Card.Title>
              <h3>${currentSavings.toFixed(2)} of ${savingsGoal.toFixed(2)}</h3>
              <ProgressBar 
                now={savingsProgress} 
                variant={savingsProgress >= 100 ? 'success' : 'primary'}
                className="mb-2"
              />
              <Button variant="outline-primary" size="sm">
                Adjust Goal
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Charts and Recent Transactions */}
      <Row className="mb-4">
        <Col md={6}>
          <Card className="h-100">
            <Card.Body>
              <Card.Title>Spending Breakdown</Card.Title>
              <div style={{ height: '300px' }}>
                <Doughnut data={doughnutData} />
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={6}>
          <Card className="h-100">
            <Card.Body>
              <Card.Title>Monthly Trends</Card.Title>
              <div style={{ height: '300px' }}>
                <Bar data={barData} options={barOptions} />
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Recent Transactions and Notifications */}
      <Row>
        <Col md={8}>
          <Card>
            <Card.Body>
              <div className="d-flex justify-content-between align-items-center mb-3">
                <Card.Title>Recent Transactions</Card.Title>
                <Dropdown>
                  <Dropdown.Toggle variant="light" id="dropdown-transactions">
                    <ThreeDots />
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                    <Dropdown.Item href="/transactions">View All</Dropdown.Item>
                    <Dropdown.Item>Export</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </div>
              
              <div className="transaction-list">
                {transactions.slice(0, 5).map(transaction => (
                  <div key={transaction.id} className="transaction-item">
                    <div className="transaction-icon">
                      {transaction.type === 'income' ? (
                        <span className="text-success">+</span>
                      ) : (
                        <span className="text-danger">-</span>
                      )}
                    </div>
                    <div className="transaction-details">
                      <div className="transaction-description">
                        {transaction.description}
                      </div>
                      <div className="transaction-category">
                        {transaction.category}
                      </div>
                    </div>
                    <div className="transaction-amount">
                      ${transaction.amount}
                    </div>
                  </div>
                ))}
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card>
            <Card.Body>
              <div className="d-flex justify-content-between align-items-center mb-3">
                <Card.Title>
                  <Bell className="me-2" />
                  Notifications
                </Card.Title>
                <span className="badge bg-primary">
                  {notifications.length}
                </span>
              </div>
              
              {notifications.length > 0 ? (
                notifications.slice(0, 3).map(notification => (
                  <Alert 
                    key={notification.id}
                    variant={notification.type === 'warning' ? 'warning' : 'info'}
                    className="p-2 mb-2"
                  >
                    <div className="d-flex justify-content-between">
                      <div>
                        <strong>{notification.title}</strong>
                        <p className="mb-0 small">{notification.message}</p>
                      </div>
                      <small className="text-muted">
                        {new Date(notification.date).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                      </small>
                    </div>
                  </Alert>
                ))
              ) : (
                <p className="text-muted">No new notifications</p>
              )}
              
              <Button variant="outline-primary" size="sm" className="mt-2">
                View All
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Dashboard;
