import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes, faPlus, faMinus } from '@fortawesome/free-solid-svg-icons';
import './TransactionForm.css';

const TransactionForm = ({ onClose, onSubmit, initialData }) => {
  const [transaction, setTransaction] = useState({
    type: 'expense',
    amount: '',
    description: '',
    category: 'food',
    date: new Date().toISOString().split('T')[0]
  });

  const expenseCategories = [
    'food', 'transportation', 'housing', 
    'entertainment', 'utilities', 'health', 
    'education', 'other'
  ];

  useEffect(() => {
    if (initialData) {
      setTransaction({
        type: initialData.type,
        amount: initialData.amount,
        description: initialData.description,
        category: initialData.category,
        date: initialData.date.split('T')[0]
      });
    }
  }, [initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTransaction(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!transaction.amount || isNaN(transaction.amount)) {
      alert('Please enter a valid amount');
      return;
    }

    if (!transaction.description.trim()) {
      alert('Please enter a description');
      return;
    }

    try {
      await onSubmit({
        ...transaction,
        amount: parseFloat(transaction.amount),
        date: new Date(transaction.date).toISOString()
      });
      onClose();
    } catch (error) {
      console.error('Error saving transaction:', error);
      alert('Failed to save transaction. Please try again.');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h3>{initialData ? 'Edit Transaction' : 'Add New Transaction'}</h3>
          <button 
            className="btn-icon" 
            onClick={onClose}
            aria-label="Close"
          >
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Type</label>
            <select
              name="type"
              value={transaction.type}
              onChange={handleChange}
            >
              <option value="income">Income</option>
              <option value="expense">Expense</option>
            </select>
          </div>
          
          <div className="form-group">
            <label>Amount</label>
            <input
              type="number"
              name="amount"
              step="0.01"
              min="0.01"
              value={transaction.amount}
              onChange={handleChange}
              placeholder="0.00"
              required
            />
          </div>
          
          <div className="form-group">
            <label>Description</label>
            <input
              type="text"
              name="description"
              value={transaction.description}
              onChange={handleChange}
              placeholder="What was this for?"
              required
            />
          </div>
          
          <div className="form-group">
            <label>Category</label>
            {transaction.type === 'expense' ? (
              <select
                name="category"
                value={transaction.category}
                onChange={handleChange}
              >
                {expenseCategories.map(cat => (
                  <option key={cat} value={cat}>
                    {cat.charAt(0).toUpperCase() + cat.slice(1)}
                  </option>
                ))}
              </select>
            ) : (
              <input
                type="text"
                name="category"
                value={transaction.category}
                onChange={handleChange}
                placeholder="Income category (e.g., Salary, Bonus)"
                required
              />
            )}
          </div>
          
          <div className="form-group">
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={transaction.date}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-actions">
            <button type="submit" className="btn btn-primary">
              {transaction.type === 'income' ? (
                <FontAwesomeIcon icon={faPlus} />
              ) : (
                <FontAwesomeIcon icon={faMinus} />
              )}
              {` ${initialData ? 'Update' : 'Add'} ${transaction.type.charAt(0).toUpperCase() + transaction.type.slice(1)}`}
            </button>
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={onClose}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TransactionForm;
