import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { 
  Chart as ChartJS, 
  ArcElement, 
  Tooltip, 
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement
} from 'chart.js';
import { Doughnut, Bar, Line, Pie } from 'react-chartjs-2';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faHome, 
  faMoneyBillWave, 
  faChartLine, 
  faUser, 
  faBell,
  faWallet,
  faExchangeAlt,
  faFileAlt,
  faUtensils,
  faCar,
  faHouse,
  faFilm,
  faBolt,
  faPlus,
  faMinus,
  faSearch,
  faCheck,
  faTimes,
  faPaperPlane,
  faSignOutAlt
} from '@fortawesome/free-solid-svg-icons';
import './Dashboard.css';

// Register Chart.js components
ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement
);

const Dashboard = () => {
  const [activeMenu, setActiveMenu] = useState('dashboard');
  const [dashboardData, setDashboardData] = useState(null);
  const [chatbotOpen, setChatbotOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([
    { sender: 'bot', text: 'Hello! I\'m your BudgetBuddy assistant. How can I help you today?', timestamp: new Date().toLocaleTimeString() }
  ]);
  const [userMessage, setUserMessage] = useState('');
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [profileForm, setProfileForm] = useState({
    first_name: '',
    last_name: '',
    email: ''
  });
  const [notifications, setNotifications] = useState([]);
  const [expenseCategories, setExpenseCategories] = useState([]);
  const [expenseTransactions, setExpenseTransactions] = useState([]);
  const [budgets, setBudgets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [newTransaction, setNewTransaction] = useState({
    type: 'expense',
    amount: '',
    description: '',
    category: 'food'
  });
  const [showTransactionForm, setShowTransactionForm] = useState(false);
  const navigate = useNavigate();

  // Fetch all dashboard data
  useEffect(() => {
    const fetchDashboardData = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        // Fetch dashboard data
        const dashboardResponse = await axios.get('http://localhost:8000/api/dashboard/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        
        // Process dashboard data
        const processedData = {
          ...dashboardResponse.data,
          savings_goal: {
            ...dashboardResponse.data.savings_goal,
            current_amount: Number(dashboardResponse.data.savings_goal.current_amount),
            target_amount: Number(dashboardResponse.data.savings_goal.target_amount)
          }
        };
        
        setDashboardData(processedData);
        
        // Process transactions and categories
        if (dashboardResponse.data.recent_transactions) {
          const expenses = dashboardResponse.data.recent_transactions.filter(t => t.type === 'expense');
          setExpenseTransactions(expenses);
          
          const categoryTotals = {};
          expenses.forEach(expense => {
            if (!categoryTotals[expense.category]) {
              categoryTotals[expense.category] = 0;
            }
            categoryTotals[expense.category] += Number(expense.amount);
          });
          
          setExpenseCategories(Object.entries(categoryTotals).map(([name, amount]) => ({
            name,
            amount,
            icon: getCategoryIcon(name)
          })));
        }

        // Fetch user data
        const userResponse = await axios.get('http://localhost:8000/api/user/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUser(userResponse.data);
        setProfileForm({
          first_name: userResponse.data.first_name || '',
          last_name: userResponse.data.last_name || '',
          email: userResponse.data.email
        });

        // Fetch notifications
        const notificationsResponse = await axios.get('http://localhost:8000/api/notifications/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setNotifications(notificationsResponse.data);

        // Mock budgets data
        setBudgets([
          { id: 1, category: 'food', limit: 500, spent: 320 },
          { id: 2, category: 'transportation', limit: 300, spent: 180 },
          { id: 3, category: 'entertainment', limit: 200, spent: 150 },
          { id: 4, category: 'utilities', limit: 400, spent: 380 }
        ]);

      } catch (error) {
        console.error('Error fetching data:', error);
        localStorage.removeItem('access_token');
        navigate('/login');
      }
    };

    fetchDashboardData();
  }, [navigate]);

  // Helper functions
  const getCategoryIcon = (category) => {
    switch (category) {
      case 'food': return faUtensils;
      case 'transportation': return faCar;
      case 'housing': return faHouse;
      case 'entertainment': return faFilm;
      case 'utilities': return faBolt;
      default: return faMoneyBillWave;
    }
  };

  const formatMoney = (amount) => {
    return amount.toLocaleString('en-US', {
      style: 'decimal',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    });
  };

  // Chatbot functions
  const handleChatSubmit = (e) => {
    e.preventDefault();
    if (!userMessage.trim()) return;
    
    const newUserMessage = {
      sender: 'user',
      text: userMessage,
      timestamp: new Date().toLocaleTimeString()
    };
    
    setChatMessages([...chatMessages, newUserMessage]);
    setUserMessage('');
    
    setTimeout(() => {
      const botResponses = [
        "I can help you with your budgeting questions.",
        "Would you like to see your recent transactions?",
        "Your savings goal is on track!",
        "I noticed you've been spending more on dining out this month.",
        "You can ask me about your financial reports."
      ];
      
      const botMessage = {
        sender: 'bot',
        text: botResponses[Math.floor(Math.random() * botResponses.length)],
        timestamp: new Date().toLocaleTimeString()
      };
      
      setChatMessages(prev => [...prev, botMessage]);
    }, 1000);
  };

  // Profile functions
  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('access_token');
    try {
      await axios.patch('http://localhost:8000/api/user/', profileForm, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(profileForm);
      setEditMode(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  // Notification functions
  const markNotificationAsRead = async (id) => {
    const token = localStorage.getItem('access_token');
    try {
      await axios.patch(`http://localhost:8000/api/notifications/${id}/`, { is_read: true }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setNotifications(notifications.map(n => 
        n.id === id ? { ...n, is_read: true } : n
      ));
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  // Transaction functions
  const handleTransactionSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('access_token');
    try {
      const response = await axios.post('http://localhost:8000/api/transactions/', newTransaction, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      
      // Update local state
      setExpenseTransactions([response.data, ...expenseTransactions]);
      
      // Update category totals
      const categoryIndex = expenseCategories.findIndex(c => c.name === newTransaction.category);
      if (categoryIndex >= 0) {
        const updatedCategories = [...expenseCategories];
        updatedCategories[categoryIndex].amount += Number(newTransaction.amount);
        setExpenseCategories(updatedCategories);
      } else {
        setExpenseCategories([...expenseCategories, {
          name: newTransaction.category,
          amount: Number(newTransaction.amount),
          icon: getCategoryIcon(newTransaction.category)
        }]);
      }
      
      // Reset form
      setNewTransaction({
        type: 'expense',
        amount: '',
        description: '',
        category: 'food'
      });
      setShowTransactionForm(false);
    } catch (error) {
      console.error('Error adding transaction:', error);
    }
  };

  // Filter transactions
  const filteredExpenses = expenseTransactions.filter(expense => {
    const matchesSearch = expense.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || expense.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('access_token');
    navigate('/login');
  };

  if (!dashboardData || !user) {
    return <div className="loading">Loading...</div>;
  }

  const { 
    income, 
    expenses, 
    savings_goal, 
    recent_transactions
  } = dashboardData;

  const savings = income - expenses;
  const progressPercentage = savings_goal.target_amount > 0 
    ? (savings_goal.current_amount / savings_goal.target_amount) * 100 
    : 0;

  // Chart data
  const doughnutData = {
    labels: ['Income', 'Expenses'],
    datasets: [
      {
        label: 'Amount',
        data: [income, expenses],
        backgroundColor: ['#4CAF50', '#F44336'],
        hoverBackgroundColor: ['#66BB6A', '#EF5350'],
      },
    ],
  };

  const monthlyData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul'],
    datasets: [
      {
        label: 'Income',
        data: [3000, 2800, 3200, 3500, 3700, 4000, 4200],
        borderColor: '#4CAF50',
        backgroundColor: 'rgba(76, 175, 80, 0.1)',
        tension: 0.3,
        fill: true
      },
      {
        label: 'Expenses',
        data: [2200, 2400, 2600, 2800, 3000, 3200, 3400],
        borderColor: '#F44336',
        backgroundColor: 'rgba(244, 67, 54, 0.1)',
        tension: 0.3,
        fill: true
      }
    ]
  };

  const categoryData = {
    labels: expenseCategories.map(cat => cat.name),
    datasets: [{
      label: 'Expenses by Category',
      data: expenseCategories.map(cat => cat.amount),
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
        '#4BC0C0',
        '#9966FF',
        '#FF9F40'
      ]
    }]
  };

  const budgetData = {
    labels: budgets.map(budget => budget.category),
    datasets: [{
      label: 'Budget Usage',
      data: budgets.map(budget => (budget.spent / budget.limit) * 100),
      backgroundColor: budgets.map(budget => 
        (budget.spent / budget.limit) > 0.9 ? '#FF6384' : 
        (budget.spent / budget.limit) > 0.7 ? '#FFCE56' : '#4CAF50'
      )
    }]
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>BudgetBuddy</h2>
        </div>
        
        <ul className="sidebar-menu">
          <li 
            className={activeMenu === 'dashboard' ? 'active' : ''}
            onClick={() => setActiveMenu('dashboard')}
          >
            <FontAwesomeIcon icon={faHome} />
            <span>Dashboard</span>
          </li>
          
          <li 
            className={activeMenu === 'expenses' ? 'active' : ''}
            onClick={() => setActiveMenu('expenses')}
          >
            <FontAwesomeIcon icon={faMoneyBillWave} />
            <span>Expenses</span>
          </li>
          
          <li 
            className={activeMenu === 'overview' ? 'active' : ''}
            onClick={() => setActiveMenu('overview')}
          >
            <FontAwesomeIcon icon={faChartLine} />
            <span>Overview</span>
          </li>
          
          <li 
            className={activeMenu === 'profile' ? 'active' : ''}
            onClick={() => setActiveMenu('profile')}
          >
            <FontAwesomeIcon icon={faUser} />
            <span>Profile</span>
          </li>
          
          <li 
            className={activeMenu === 'notifications' ? 'active' : ''}
            onClick={() => setActiveMenu('notifications')}
          >
            <FontAwesomeIcon icon={faBell} />
            <span>Notifications</span>
            {notifications.filter(n => !n.is_read).length > 0 && (
              <span className="notification-badge">
                {notifications.filter(n => !n.is_read).length}
              </span>
            )}
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Header */}
        <div className="content-header">
          <h1>{activeMenu.charAt(0).toUpperCase() + activeMenu.slice(1)}</h1>
          <div className="header-actions">
            {activeMenu === 'expenses' && (
              <button 
                className="btn btn-primary"
                onClick={() => setShowTransactionForm(true)}
              >
                <FontAwesomeIcon icon={faPlus} /> Add Transaction
              </button>
            )}
            <button className="btn btn-secondary" onClick={handleLogout}>
              <FontAwesomeIcon icon={faSignOutAlt} /> Logout
            </button>
          </div>
        </div>

        {/* Dashboard Content */}
        {activeMenu === 'dashboard' && (
          <div className="dashboard-content">
            {/* Summary Cards */}
            <div className="summary-cards">
              <div className="card income-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faWallet} />
                </div>
                <div className="card-content">
                  <h3>Income</h3>
                  <p>${formatMoney(income)}</p>
                </div>
              </div>
              
              <div className="card expense-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faMoneyBillWave} />
                </div>
                <div className="card-content">
                  <h3>Expenses</h3>
                  <p>${formatMoney(expenses)}</p>
                </div>
              </div>
              
              <div className="card savings-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faExchangeAlt} />
                </div>
                <div className="card-content">
                  <h3>Savings</h3>
                  <p>${formatMoney(savings)}</p>
                </div>
              </div>
              
              <div className="card goal-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faFileAlt} />
                </div>
                <div className="card-content">
                  <h3>Savings Goal</h3>
                  <p>${formatMoney(savings_goal.current_amount)} / ${formatMoney(savings_goal.target_amount)}</p>
                  <div className="progress-bar">
                    <div 
                      className="progress" 
                      style={{ width: `${progressPercentage}%` }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>

            {/* Charts Section */}
            <div className="charts-section">
              <div className="chart-container">
                <h3>Income vs Expenses</h3>
                <Doughnut data={doughnutData} />
              </div>
              
              <div className="chart-container">
                <h3>Monthly Trend</h3>
                <Line data={monthlyData} />
              </div>
              
              <div className="chart-container">
                <h3>Expense Categories</h3>
                <Bar data={categoryData} />
              </div>
            </div>

            {/* Recent Transactions */}
            <div className="recent-transactions">
              <h3>Recent Transactions</h3>
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Description</th>
                    <th>Category</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {recent_transactions.map((transaction) => (
                    <tr key={transaction.id}>
                      <td>{new Date(transaction.date).toLocaleDateString()}</td>
                      <td>{transaction.description}</td>
                      <td>{transaction.category}</td>
                      <td className={transaction.type === 'income' ? 'income' : 'expense'}>
                        {transaction.type === 'income' ? '+' : '-'}${formatMoney(Number(transaction.amount))}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Expenses Content */}
        {activeMenu === 'expenses' && (
          <div className="expenses-content">
            {/* Transaction Form Modal */}
            {showTransactionForm && (
              <div className="modal-overlay">
                <div className="modal">
                  <div className="modal-header">
                    <h3>Add New Transaction</h3>
                    <button 
                      className="btn-icon" 
                      onClick={() => setShowTransactionForm(false)}
                    >
                      <FontAwesomeIcon icon={faTimes} />
                    </button>
                  </div>
                  
                  <form onSubmit={handleTransactionSubmit}>
                    <div className="form-group">
                      <label>Type</label>
                      <select
                        value={newTransaction.type}
                        onChange={(e) => setNewTransaction({...newTransaction, type: e.target.value})}
                      >
                        <option value="income">Income</option>
                        <option value="expense">Expense</option>
                      </select>
                    </div>
                    
                    <div className="form-group">
                      <label>Amount</label>
                      <input
                        type="number"
                        step="0.01"
                        value={newTransaction.amount}
                        onChange={(e) => setNewTransaction({...newTransaction, amount: e.target.value})}
                        placeholder="0.00"
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Description</label>
                      <input
                        type="text"
                        value={newTransaction.description}
                        onChange={(e) => setNewTransaction({...newTransaction, description: e.target.value})}
                        placeholder="What was this for?"
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Category</label>
                      <select
                        value={newTransaction.category}
                        onChange={(e) => setNewTransaction({...newTransaction, category: e.target.value})}
                      >
                        <option value="food">Food</option>
                        <option value="transportation">Transportation</option>
                        <option value="housing">Housing</option>
                        <option value="entertainment">Entertainment</option>
                        <option value="utilities">Utilities</option>
                        <option value="other">Other</option>
                      </select>
                    </div>
                    
                    <div className="form-actions">
                      <button type="submit" className="btn btn-primary">Add Transaction</button>
                      <button 
                        type="button" 
                        className="btn btn-secondary"
                        onClick={() => setShowTransactionForm(false)}
                      >
                        Cancel
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            )}

            {/* Filters */}
            <div className="filters">
              <div className="search-box">
                <FontAwesomeIcon icon={faSearch} />
                <input 
                  type="text" 
                  placeholder="Search expenses..." 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="all">All Categories</option>
                {expenseCategories.map(category => (
                  <option key={category.name} value={category.name}>
                    {category.name.charAt(0).toUpperCase() + category.name.slice(1)}
                  </option>
                ))}
              </select>
            </div>
            
            {/* Transactions Table */}
            <div className="transactions-table">
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Description</th>
                    <th>Category</th>
                    <th>Amount</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredExpenses.map((expense) => (
                    <tr key={expense.id}>
                      <td>{new Date(expense.date).toLocaleDateString()}</td>
                      <td>{expense.description}</td>
                      <td>
                        <span className="category-tag">
                          <FontAwesomeIcon icon={getCategoryIcon(expense.category)} />
                          {expense.category}
                        </span>
                      </td>
                      <td className="expense">-${formatMoney(Number(expense.amount))}</td>
                      <td>
                        <button className="btn-icon">
                          <FontAwesomeIcon icon={faFileAlt} />
                        </button>
                        <button className="btn-icon">
                          <FontAwesomeIcon icon={faMinus} />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            {/* Category Summary */}
            <div className="category-summary">
              <h3>Expense Categories</h3>
              <div className="categories-grid">
                {expenseCategories.map(category => (
                  <div key={category.name} className="category-card">
                    <div className="category-icon">
                      <FontAwesomeIcon icon={category.icon} />
                    </div>
                    <h4>{category.name.charAt(0).toUpperCase() + category.name.slice(1)}</h4>
                    <p>${formatMoney(category.amount)}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Overview Content */}
        {activeMenu === 'overview' && (
          <div className="overview-content">
            <div className="charts-section">
              <div className="chart-container">
                <h3>Financial Overview</h3>
                <Doughnut data={doughnutData} />
              </div>
              
              <div className="chart-container">
                <h3>Monthly Cash Flow</h3>
                <Line data={monthlyData} />
              </div>
              
              <div className="chart-container">
                <h3>Budget Progress</h3>
                <Bar 
                  data={budgetData} 
                  options={{
                    scales: {
                      y: {
                        beginAtZero: true,
                        max: 100,
                        ticks: {
                          callback: value => `${value}%`
                        }
                      }
                    }
                  }}
                />
              </div>
            </div>
            
            <div className="budget-summary">
              <h3>Budget Summary</h3>
              <div className="budgets-grid">
                {budgets.map(budget => {
                  const percentage = (budget.spent / budget.limit) * 100;
                  const isOverBudget = percentage > 100;
                  
                  return (
                    <div key={budget.id} className="budget-card">
                      <div className="budget-header">
                        <div className="budget-icon">
                          <FontAwesomeIcon icon={getCategoryIcon(budget.category)} />
                        </div>
                        <h4>{budget.category.charAt(0).toUpperCase() + budget.category.slice(1)}</h4>
                      </div>
                      
                      <div className="budget-progress">
                        <div className="progress-info">
                          <span>${formatMoney(budget.spent)} of ${formatMoney(budget.limit)}</span>
                          <span>{Math.round(percentage)}%</span>
                        </div>
                        <div className="progress-bar">
                          <div 
                            className={`progress ${isOverBudget ? 'over' : ''}`}
                            style={{ width: `${Math.min(percentage, 100)}%` }}
                          ></div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        )}

        {/* Profile Content */}
        {activeMenu === 'profile' && (
          <div className="profile-content">
            <h2>Profile Information</h2>
            
            {editMode ? (
              <form onSubmit={handleProfileUpdate}>
                <div className="form-group">
                  <label>First Name</label>
                  <input
                    type="text"
                    name="first_name"
                    value={profileForm.first_name}
                    onChange={(e) => setProfileForm({...profileForm, first_name: e.target.value})}
                  />
                </div>
                
                <div className="form-group">
                  <label>Last Name</label>
                  <input
                    type="text"
                    name="last_name"
                    value={profileForm.last_name}
                    onChange={(e) => setProfileForm({...profileForm, last_name: e.target.value})}
                  />
                </div>
                
                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="email"
                    name="email"
                    value={profileForm.email}
                    onChange={(e) => setProfileForm({...profileForm, email: e.target.value})}
                    disabled
                  />
                </div>
                
                <div className="form-actions">
                  <button type="submit" className="btn btn-primary">Save Changes</button>
                  <button 
                    type="button" 
                    className="btn btn-secondary"
                    onClick={() => setEditMode(false)}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            ) : (
              <div className="profile-info">
                <p><strong>Username:</strong> {user.username}</p>
                <p><strong>First Name:</strong> {user.first_name || 'Not set'}</p>
                <p><strong>Last Name:</strong> {user.last_name || 'Not set'}</p>
                <p><strong>Email:</strong> {user.email}</p>
                
                <button 
                  className="btn btn-primary" 
                  onClick={() => setEditMode(true)}
                >
                  Edit Profile
                </button>
              </div>
            )}
          </div>
        )}

        {/* Notifications Content */}
        {activeMenu === 'notifications' && (
          <div className="notifications-content">
            <h2>
              <FontAwesomeIcon icon={faBell} /> Notifications
            </h2>
            
            {notifications.length === 0 ? (
              <p>No notifications to display</p>
            ) : (
              <ul className="notifications-list">
                {notifications.map(notification => (
                  <li 
                    key={notification.id} 
                    className={`notification ${notification.notification_type} ${notification.is_read ? 'read' : 'unread'}`}
                  >
                    <div className="notification-content">
                      <h4>{notification.title}</h4>
                      <p>{notification.message}</p>
                      <small>{new Date(notification.created_at).toLocaleString()}</small>
                    </div>
                    
                    {!notification.is_read && (
                      <button 
                        className="btn-icon" 
                        onClick={() => markNotificationAsRead(notification.id)}
                        title="Mark as read"
                      >
                        <FontAwesomeIcon icon={faCheck} />
                      </button>
                    )}
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>

      {/* Chatbot */}
      <div className={`chatbot-container ${chatbotOpen ? 'open' : ''}`}>
        <div className="chatbot-header" onClick={() => setChatbotOpen(!chatbotOpen)}>
          <h3>BudgetBuddy Assistant</h3>
          <button 
            className="btn-icon" 
            onClick={(e) => {
              e.stopPropagation();
              setChatbotOpen(false);
            }}
          >
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>
        
        {chatbotOpen && (
          <div className="chatbot-content">
            <div className="chat-messages">
              {chatMessages.map((message, index) => (
                <div key={index} className={`message ${message.sender}`}>
                  <p>{message.text}</p>
                  <small>{message.timestamp}</small>
                </div>
              ))}
            </div>
            
            <form onSubmit={handleChatSubmit} className="chat-input">
              <input
                type="text"
                value={userMessage}
                onChange={(e) => setUserMessage(e.target.value)}
                placeholder="Ask me about your budget..."
              />
              <button type="submit">
                <FontAwesomeIcon icon={faPaperPlane} />
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
