import React, { useState, useEffect } from 'react';
import { useNavigate, Outlet } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faPlus, 
  faFilePdf,
  faSignOutAlt,
  faHome,
  faChartBar,
  faMoneyBillWave,
  faCog,
  faBell
} from '@fortawesome/free-solid-svg-icons';
import TransactionForm from './TransactionForm';
import SummaryCards from './SummaryCards';
import TransactionsTable from './TransactionsTable';
import BudgetSummary from './BudgetSummary';
import NotificationsList from './NotificationsList';
import './Dashboard.css';

const Dashboard = () => {
  const [activeMenu, setActiveMenu] = useState('dashboard');
  const [showTransactionForm, setShowTransactionForm] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('access_token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get('http://localhost:8000/api/dashboard/', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setDashboardData(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch dashboard data');
        console.error('Dashboard error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    navigate('/login');
  };

  const handleAddTransaction = async (transactionData) => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.post('http://localhost:8000/api/transactions/', transactionData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Refresh data after successful addition
      const response = await axios.get('http://localhost:8000/api/dashboard/', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDashboardData(response.data);
      setShowTransactionForm(false);
    } catch (err) {
      console.error('Error adding transaction:', err);
      alert('Failed to add transaction. Please try again.');
    }
  };

  if (loading) return <div className="loading">Loading dashboard...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  const { income, expenses, savings_goal, recent_transactions, notifications } = dashboardData;

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>BudgetBuddy</h2>
        </div>
        <nav className="sidebar-nav">
          <button 
            className={activeMenu === 'dashboard' ? 'active' : ''}
            onClick={() => setActiveMenu('dashboard')}
          >
            <FontAwesomeIcon icon={faHome} /> Dashboard
          </button>
          <button 
            className={activeMenu === 'transactions' ? 'active' : ''}
            onClick={() => setActiveMenu('transactions')}
          >
            <FontAwesomeIcon icon={faMoneyBillWave} /> Transactions
          </button>
          <button 
            className={activeMenu === 'budgets' ? 'active' : ''}
            onClick={() => setActiveMenu('budgets')}
          >
            <FontAwesomeIcon icon={faChartBar} /> Budgets
          </button>
          <button 
            className={activeMenu === 'notifications' ? 'active' : ''}
            onClick={() => setActiveMenu('notifications')}
          >
            <FontAwesomeIcon icon={faBell} /> Notifications
            {notifications?.filter(n => !n.is_read).length > 0 && (
              <span className="notification-badge">
                {notifications.filter(n => !n.is_read).length}
              </span>
            )}
          </button>
          <button 
            className={activeMenu === 'settings' ? 'active' : ''}
            onClick={() => setActiveMenu('settings')}
          >
            <FontAwesomeIcon icon={faCog} /> Settings
          </button>
        </nav>
        <button className="logout-btn" onClick={handleLogout}>
          <FontAwesomeIcon icon={faSignOutAlt} /> Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header className="content-header">
          <h1>{activeMenu.charAt(0).toUpperCase() + activeMenu.slice(1)}</h1>
          <div className="header-actions">
            {activeMenu === 'transactions' && (
              <button 
                className="btn btn-primary"
                onClick={() => setShowTransactionForm(true)}
              >
                <FontAwesomeIcon icon={faPlus} /> Add Transaction
              </button>
            )}
            {activeMenu === 'dashboard' && (
              <button 
                className="btn btn-secondary"
                onClick={() => {/* Add report generation logic */}}
              >
                <FontAwesomeIcon icon={faFilePdf} /> Generate Report
              </button>
            )}
          </div>
        </header>

        <div className="content-area">
          {activeMenu === 'dashboard' && (
            <>
              <SummaryCards 
                income={income}
                expenses={expenses}
                savings={income - expenses}
                savingsGoal={savings_goal}
              />
              <TransactionsTable 
                transactions={recent_transactions}
                title="Recent Transactions"
              />
            </>
          )}

          {activeMenu === 'transactions' && (
            <TransactionsTable 
              transactions={recent_transactions}
              title="All Transactions"
              showFilters={true}
              onAddTransaction={() => setShowTransactionForm(true)}
            />
          )}

          {activeMenu === 'budgets' && (
            <BudgetSummary />
          )}

          {activeMenu === 'notifications' && (
            <NotificationsList notifications={notifications} />
          )}

          {activeMenu === 'settings' && (
            <div className="settings-container">
              <h2>Settings</h2>
              <p>Manage your account preferences here.</p>
            </div>
          )}

          <Outlet /> {/* For nested routes */}
        </div>
      </div>

      {/* Transaction Form Modal */}
      {showTransactionForm && (
        <TransactionForm 
          onClose={() => setShowTransactionForm(false)}
          onSubmit={handleAddTransaction}
        />
      )}
    </div>
  );
};

export default Dashboard;
