import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { 
  Chart as ChartJS, 
  ArcElement, 
  Tooltip, 
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement
} from 'chart.js';
import { Doughnut, Bar, Line } from 'react-chartjs-2';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faHome, 
  faMoneyBillWave, 
  faChartLine, 
  faUser, 
  faBell,
  faWallet,
  faExchangeAlt,
  faFileAlt
} from '@fortawesome/free-solid-svg-icons';
import './Dashboard.css';

// Register Chart.js components
ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement
);

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [activeMenu, setActiveMenu] = useState('dashboard');
  const [activeSubMenu, setActiveSubMenu] = useState(null);
  const [chatbotOpen, setChatbotOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [userMessage, setUserMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDashboardData = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await axios.get('http://localhost:8000/api/dashboard/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setDashboardData(response.data);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        localStorage.removeItem('access_token');
        navigate('/login');
      }
    };

    fetchDashboardData();
  }, [navigate]);

  const handleChatSubmit = (e) => {
    e.preventDefault();
    if (!userMessage.trim()) return;
    
    // Add user message to chat
    const newUserMessage = {
      sender: 'user',
      text: userMessage,
      timestamp: new Date().toLocaleTimeString()
    };
    
    setChatMessages([...chatMessages, newUserMessage]);
    setUserMessage('');
    
    // Simulate bot response
    setTimeout(() => {
      const botResponses = [
        "I can help you with your budgeting questions.",
        "Would you like to see your recent transactions?",
        "Your savings goal is on track!",
        "I noticed you've been spending more on dining out this month.",
        "You can ask me about your financial reports."
      ];
      
      const botMessage = {
        sender: 'bot',
        text: botResponses[Math.floor(Math.random() * botResponses.length)],
        timestamp: new Date().toLocaleTimeString()
      };
      
      setChatMessages(prev => [...prev, botMessage]);
    }, 1000);
  };

  if (!dashboardData) {
    return <div className="loading">Loading...</div>;
  }

  const { 
    income, 
    expenses, 
    savings_goal, 
    recent_transactions, 
    notifications 
  } = dashboardData;

  const savings = income - expenses;
  const progressPercentage = savings_goal.target_amount > 0 
    ? (savings_goal.current_amount / savings_goal.target_amount) * 100 
    : 0;

  // Prepare data for charts
  const doughnutData = {
    labels: ['Income', 'Expenses'],
    datasets: [
      {
        label: 'Amount',
        data: [income, expenses],
        backgroundColor: ['#4CAF50', '#F44336'],
        hoverBackgroundColor: ['#66BB6A', '#EF5350'],
      },
    ],
  };

  const monthlyData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul'],
    datasets: [
      {
        label: 'Income',
        data: [3000, 2800, 3200, 3500, 3700, 4000, 4200],
        borderColor: '#4CAF50',
        backgroundColor: 'rgba(76, 175, 80, 0.1)',
        tension: 0.3,
        fill: true
      },
      {
        label: 'Expenses',
        data: [2200, 2400, 2600, 2800, 3000, 3200, 3400],
        borderColor: '#F44336',
        backgroundColor: 'rgba(244, 67, 54, 0.1)',
        tension: 0.3,
        fill: true
      }
    ]
  };

  const categoryData = {
    labels: ['Food', 'Transport', 'Housing', 'Entertainment', 'Utilities'],
    datasets: [{
      label: 'Expenses by Category',
      data: [500, 300, 800, 200, 400],
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
        '#4BC0C0',
        '#9966FF'
      ]
    }]
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>BudgetBuddy</h2>
        </div>
        
        <ul className="sidebar-menu">
          <li 
            className={activeMenu === 'dashboard' ? 'active' : ''}
            onClick={() => setActiveMenu('dashboard')}
          >
            <FontAwesomeIcon icon={faHome} />
            <span>Dashboard</span>
          </li>
          
          <li 
            className={activeMenu === 'expenses' ? 'active' : ''}
            onClick={() => {
              setActiveMenu('expenses');
              setActiveSubMenu(activeSubMenu === 'expenses' ? null : 'expenses');
            }}
          >
            <FontAwesomeIcon icon={faMoneyBillWave} />
            <span>Expenses</span>
            <span className="arrow">{activeSubMenu === 'expenses' ? '▼' : '▶'}</span>
          </li>
          
          {activeSubMenu === 'expenses' && (
            <ul className="submenu">
              <li>Budgets</li>
              <li>Statistics</li>
              <li>Reports</li>
            </ul>
          )}
          
          <li 
            className={activeMenu === 'overview' ? 'active' : ''}
            onClick={() => {
              setActiveMenu('overview');
              setActiveSubMenu(activeSubMenu === 'overview' ? null : 'overview');
            }}
          >
            <FontAwesomeIcon icon={faChartLine} />
            <span>Overview</span>
            <span className="arrow">{activeSubMenu === 'overview' ? '▼' : '▶'}</span>
          </li>
          
          {activeSubMenu === 'overview' && (
            <ul className="submenu">
              <li>Transactions</li>
              <li>Account Summary</li>
              <li>Savings Goals</li>
            </ul>
          )}
          
          <li 
            className={activeMenu === 'profile' ? 'active' : ''}
            onClick={() => setActiveMenu('profile')}
          >
            <FontAwesomeIcon icon={faUser} />
            <span>Profile</span>
          </li>
          
          <li 
            className={activeMenu === 'notifications' ? 'active' : ''}
            onClick={() => setActiveMenu('notifications')}
          >
            <FontAwesomeIcon icon={faBell} />
            <span>Notifications</span>
            {notifications.length > 0 && (
              <span className="notification-badge">{notifications.length}</span>
            )}
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Header */}
        <div className="content-header">
          <h1>{activeMenu.charAt(0).toUpperCase() + activeMenu.slice(1)}</h1>
          <div className="header-actions">
            <button className="btn btn-primary">Add Transaction</button>
            <button className="btn btn-secondary">Generate Report</button>
          </div>
        </div>

        {/* Dashboard Content */}
        {activeMenu === 'dashboard' && (
          <div className="dashboard-content">
            {/* Summary Cards */}
            <div className="summary-cards">
              <div className="card income-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faWallet} />
                </div>
                <div className="card-content">
                  <h3>Income</h3>
                  <p>${income.toFixed(2)}</p>
                </div>
              </div>
              
              <div className="card expense-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faMoneyBillWave} />
                </div>
                <div className="card-content">
                  <h3>Expenses</h3>
                  <p>${expenses.toFixed(2)}</p>
                </div>
              </div>
              
              <div className="card savings-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faExchangeAlt} />
                </div>
                <div className="card-content">
                  <h3>Savings</h3>
                  <p>${savings.toFixed(2)}</p>
                </div>
              </div>
              
              <div className="card goal-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faFileAlt} />
                </div>
                <div className="card-content">
                  <h3>Savings Goal</h3>
                  <p>${savings_goal.current_amount.toFixed(2)} / ${savings_goal.target_amount.toFixed(2)}</p>
                  <div className="progress-bar">
                    <div 
                      className="progress" 
                      style={{ width: `${progressPercentage}%` }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>

            {/* Charts Section */}
            <div className="charts-section">
              <div className="chart-container">
                <h3>Income vs Expenses</h3>
                <Doughnut data={doughnutData} />
              </div>
              
              <div className="chart-container">
                <h3>Monthly Trend</h3>
                <Line data={monthlyData} />
              </div>
              
              <div className="chart-container">
                <h3>Expense Categories</h3>
                <Bar data={categoryData} />
              </div>
            </div>

            {/* Recent Transactions */}
            <div className="recent-transactions">
              <h3>Recent Transactions</h3>
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Description</th>
                    <th>Category</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {recent_transactions.map((transaction) => (
                    <tr key={transaction.id}>
                      <td>{new Date(transaction.date).toLocaleDateString()}</td>
                      <td>{transaction.description}</td>
                      <td>{transaction.category}</td>
                      <td className={transaction.type === 'income' ? 'income' : 'expense'}>
                        {transaction.type === 'income' ? '+' : '-'}${transaction.amount}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Notifications */}
            <div className="notifications-section">
              <h3>Notifications</h3>
              {notifications.length > 0 ? (
                <ul>
                  {notifications.map((notification) => (
                    <li key={notification.id} className={`notification ${notification.notification_type}`}>
                      <h4>{notification.title}</h4>
                      <p>{notification.message}</p>
                      <small>{notification.formatted_date}</small>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No new notifications</p>
              )}
            </div>
          </div>
        )}

        {/* Other Menu Content */}
        {activeMenu !== 'dashboard' && (
          <div className="menu-content">
            <h2>{activeMenu.charAt(0).toUpperCase() + activeMenu.slice(1)} Content</h2>
            <p>This section would display content for {activeMenu}.</p>
          </div>
        )}
      </div>

      {/* Chatbot */}
      <div className={`chatbot-container ${chatbotOpen ? 'open' : ''}`}>
        <div className="chatbot-header" onClick={() => setChatbotOpen(!chatbotOpen)}>
          <h3>BudgetBuddy Assistant</h3>
          <span>{chatbotOpen ? '▼' : '▲'}</span>
        </div>
        
        {chatbotOpen && (
          <div className="chatbot-content">
            <div className="chat-messages">
              {chatMessages.length > 0 ? (
                chatMessages.map((message, index) => (
                  <div key={index} className={`message ${message.sender}`}>
                    <p>{message.text}</p>
                    <small>{message.timestamp}</small>
                  </div>
                ))
              ) : (
                <div className="welcome-message">
                  <p>Hi! I'm your BudgetBuddy assistant. How can I help you today?</p>
                </div>
              )}
            </div>
            
            <form onSubmit={handleChatSubmit} className="chat-input">
              <input
                type="text"
                value={userMessage}
                onChange={(e) => setUserMessage(e.target.value)}
                placeholder="Ask me about your budget..."
              />
              <button type="submit">Send</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
