import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { 
  Chart as ChartJS, 
  ArcElement, 
  Tooltip, 
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement
} from 'chart.js';
import { Doughnut, Bar, Line, Pie } from 'react-chartjs-2';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faHome, 
  faMoneyBillWave, 
  faChartLine, 
  faUser, 
  faBell,
  faWallet,
  faExchangeAlt,
  faFileAlt,
  faUtensils,
  faCar,
  faHome as faHouse,
  faFilm,
  faBolt,
  faPlus,
  faMinus,
  faSearch
} from '@fortawesome/free-solid-svg-icons';
import './Dashboard.css';

// Register Chart.js components
ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement
);

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [activeMenu, setActiveMenu] = useState('dashboard');
  const [activeSubMenu, setActiveSubMenu] = useState(null);
  const [chatbotOpen, setChatbotOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [userMessage, setUserMessage] = useState('');
  const [expenseCategories, setExpenseCategories] = useState([]);
  const [expenseTransactions, setExpenseTransactions] = useState([]);
  const [budgets, setBudgets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDashboardData = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await axios.get('http://localhost:8000/api/dashboard/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        
        // Ensure numeric values for savings goal
        const processedData = {
          ...response.data,
          savings_goal: {
            ...response.data.savings_goal,
            current_amount: Number(response.data.savings_goal.current_amount),
            target_amount: Number(response.data.savings_goal.target_amount)
          }
        };
        
        setDashboardData(processedData);
        
        // Fetch additional expense data
        if (response.data.recent_transactions) {
          const expenses = response.data.recent_transactions.filter(t => t.type === 'expense');
          setExpenseTransactions(expenses);
          
          // Calculate category totals
          const categoryTotals = {};
          expenses.forEach(expense => {
            if (!categoryTotals[expense.category]) {
              categoryTotals[expense.category] = 0;
            }
            categoryTotals[expense.category] += Number(expense.amount);
          });
          
          setExpenseCategories(Object.entries(categoryTotals).map(([name, amount]) => ({
            name,
            amount,
            icon: getCategoryIcon(name)
          })));
        }
        
        // Mock budgets data
        setBudgets([
          { id: 1, category: 'food', limit: 500, spent: 320 },
          { id: 2, category: 'transportation', limit: 300, spent: 180 },
          { id: 3, category: 'entertainment', limit: 200, spent: 150 },
          { id: 4, category: 'utilities', limit: 400, spent: 380 }
        ]);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        localStorage.removeItem('access_token');
        navigate('/login');
      }
    };

    fetchDashboardData();
  }, [navigate]);

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'food': return faUtensils;
      case 'transportation': return faCar;
      case 'housing': return faHouse;
      case 'entertainment': return faFilm;
      case 'utilities': return faBolt;
      default: return faMoneyBillWave;
    }
  };

  const handleChatSubmit = (e) => {
    e.preventDefault();
    if (!userMessage.trim()) return;
    
    // Add user message to chat
    const newUserMessage = {
      sender: 'user',
      text: userMessage,
      timestamp: new Date().toLocaleTimeString()
    };
    
    setChatMessages([...chatMessages, newUserMessage]);
    setUserMessage('');
    
    // Simulate bot response
    setTimeout(() => {
      const botResponses = [
        "I can help you with your budgeting questions.",
        "Would you like to see your recent transactions?",
        "Your savings goal is on track!",
        "I noticed you've been spending more on dining out this month.",
        "You can ask me about your financial reports."
      ];
      
      const botMessage = {
        sender: 'bot',
        text: botResponses[Math.floor(Math.random() * botResponses.length)],
        timestamp: new Date().toLocaleTimeString()
      };
      
      setChatMessages(prev => [...prev, botMessage]);
    }, 1000);
  };

  const filteredExpenses = expenseTransactions.filter(expense => {
    const matchesSearch = expense.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || expense.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const formatMoney = (amount) => {
    return amount.toLocaleString('en-US', {
      style: 'decimal',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    });
  };

  if (!dashboardData) {
    return <div className="loading">Loading...</div>;
  }

  const { 
    income, 
    expenses, 
    savings_goal, 
    recent_transactions, 
    notifications 
  } = dashboardData;

  const savings = income - expenses;
  const progressPercentage = savings_goal.target_amount > 0 
    ? (savings_goal.current_amount / savings_goal.target_amount) * 100 
    : 0;

  // Prepare data for charts
  const doughnutData = {
    labels: ['Income', 'Expenses'],
    datasets: [
      {
        label: 'Amount',
        data: [income, expenses],
        backgroundColor: ['#4CAF50', '#F44336'],
        hoverBackgroundColor: ['#66BB6A', '#EF5350'],
      },
    ],
  };

  const monthlyData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul'],
    datasets: [
      {
        label: 'Income',
        data: [3000, 2800, 3200, 3500, 3700, 4000, 4200],
        borderColor: '#4CAF50',
        backgroundColor: 'rgba(76, 175, 80, 0.1)',
        tension: 0.3,
        fill: true
      },
      {
        label: 'Expenses',
        data: [2200, 2400, 2600, 2800, 3000, 3200, 3400],
        borderColor: '#F44336',
        backgroundColor: 'rgba(244, 67, 54, 0.1)',
        tension: 0.3,
        fill: true
      }
    ]
  };

  const categoryData = {
    labels: expenseCategories.map(cat => cat.name),
    datasets: [{
      label: 'Expenses by Category',
      data: expenseCategories.map(cat => cat.amount),
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
        '#4BC0C0',
        '#9966FF',
        '#FF9F40'
      ]
    }]
  };

  const budgetData = {
    labels: budgets.map(budget => budget.category),
    datasets: [{
      label: 'Budget Usage',
      data: budgets.map(budget => (budget.spent / budget.limit) * 100),
      backgroundColor: budgets.map(budget => 
        (budget.spent / budget.limit) > 0.9 ? '#FF6384' : 
        (budget.spent / budget.limit) > 0.7 ? '#FFCE56' : '#4CAF50'
      )
    }]
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>BudgetBuddy</h2>
        </div>
        
        <ul className="sidebar-menu">
          <li 
            className={activeMenu === 'dashboard' ? 'active' : ''}
            onClick={() => setActiveMenu('dashboard')}
          >
            <FontAwesomeIcon icon={faHome} />
            <span>Dashboard</span>
          </li>
          
          <li 
            className={activeMenu === 'expenses' ? 'active' : ''}
            onClick={() => {
              setActiveMenu('expenses');
              setActiveSubMenu(activeSubMenu === 'expenses' ? null : 'expenses');
            }}
          >
            <FontAwesomeIcon icon={faMoneyBillWave} />
            <span>Expenses</span>
            <span className="arrow">{activeSubMenu === 'expenses' ? '▼' : '▶'}</span>
          </li>
          
          {activeSubMenu === 'expenses' && (
            <ul className="submenu">
              <li onClick={() => setActiveMenu('expenses-overview')}>Overview</li>
              <li onClick={() => setActiveMenu('expenses-transactions')}>Transactions</li>
              <li onClick={() => setActiveMenu('expenses-budgets')}>Budgets</li>
              <li onClick={() => setActiveMenu('expenses-categories')}>Categories</li>
            </ul>
          )}
          
          <li 
            className={activeMenu === 'overview' ? 'active' : ''}
            onClick={() => {
              setActiveMenu('overview');
              setActiveSubMenu(activeSubMenu === 'overview' ? null : 'overview');
            }}
          >
            <FontAwesomeIcon icon={faChartLine} />
            <span>Overview</span>
            <span className="arrow">{activeSubMenu === 'overview' ? '▼' : '▶'}</span>
          </li>
          
          {activeSubMenu === 'overview' && (
            <ul className="submenu">
              <li>Transactions</li>
              <li>Account Summary</li>
              <li>Savings Goals</li>
            </ul>
          )}
          
          <li 
            className={activeMenu === 'profile' ? 'active' : ''}
            onClick={() => setActiveMenu('profile')}
          >
            <FontAwesomeIcon icon={faUser} />
            <span>Profile</span>
          </li>
          
          <li 
            className={activeMenu === 'notifications' ? 'active' : ''}
            onClick={() => setActiveMenu('notifications')}
          >
            <FontAwesomeIcon icon={faBell} />
            <span>Notifications</span>
            {notifications.length > 0 && (
              <span className="notification-badge">{notifications.length}</span>
            )}
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Header */}
        <div className="content-header">
          <h1>{activeMenu.charAt(0).toUpperCase() + activeMenu.slice(1).replace('-', ' ')}</h1>
          <div className="header-actions">
            {activeMenu.includes('expenses') && (
              <>
                <button className="btn btn-primary">
                  <FontAwesomeIcon icon={faPlus} /> Add Expense
                </button>
                {activeMenu === 'expenses-budgets' && (
                  <button className="btn btn-secondary">
                    <FontAwesomeIcon icon={faPlus} /> Set Budget
                  </button>
                )}
              </>
            )}
          </div>
        </div>

        {/* Dashboard Content */}
        {activeMenu === 'dashboard' && (
          <div className="dashboard-content">
            {/* ... existing dashboard content ... */}
          </div>
        )}

        {/* Expenses Overview */}
        {activeMenu === 'expenses-overview' && (
          <div className="expenses-content">
            <div className="summary-cards">
              <div className="card expense-card">
                <div className="card-icon">
                  <FontAwesomeIcon icon={faMoneyBillWave} />
                </div>
                <div className="card-content">
                  <h3>Total Expenses</h3>
                  <p>${formatMoney(expenses)}</p>
                </div>
              </div>
              
              <div className="card">
                <div className="card-icon" style={{ backgroundColor: '#2196F3' }}>
                  <FontAwesomeIcon icon={faUtensils} />
                </div>
                <div className="card-content">
                  <h3>Top Category</h3>
                  <p>{expenseCategories[0]?.name || 'N/A'}</p>
                  <small>${formatMoney(expenseCategories[0]?.amount || 0)}</small>
                </div>
              </div>
              
              <div className="card">
                <div className="card-icon" style={{ backgroundColor: '#FFC107' }}>
                  <FontAwesomeIcon icon={faCar} />
                </div>
                <div className="card-content">
                  <h3>Average Daily</h3>
                  <p>${formatMoney(expenses / 30)}</p>
                </div>
              </div>
              
              <div className="card">
                <div className="card-icon" style={{ backgroundColor: '#9C27B0' }}>
                  <FontAwesomeIcon icon={faMinus} />
                </div>
                <div className="card-content">
                  <h3>Monthly Change</h3>
                  <p style={{ color: '#4CAF50' }}>+12%</p>
                </div>
              </div>
            </div>

            <div className="charts-section">
              <div className="chart-container">
                <h3>Expense Distribution</h3>
                <Pie data={categoryData} />
              </div>
              
              <div className="chart-container">
                <h3>Budget Progress</h3>
                <Bar 
                  data={budgetData} 
                  options={{
                    scales: {
                      y: {
                        beginAtZero: true,
                        max: 100,
                        ticks: {
                          callback: value => `${value}%`
                        }
                      }
                    }
                  }}
                />
              </div>
              
              <div className="chart-container">
                <h3>Monthly Trend</h3>
                <Line data={monthlyData} />
              </div>
            </div>
          </div>
        )}

        {/* Expenses Transactions */}
        {activeMenu === 'expenses-transactions' && (
          <div className="expenses-transactions">
            <div className="filters">
              <div className="search-box">
                <FontAwesomeIcon icon={faSearch} />
                <input 
                  type="text" 
                  placeholder="Search expenses..." 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="all">All Categories</option>
                {expenseCategories.map(category => (
                  <option key={category.name} value={category.name}>
                    {category.name.charAt(0).toUpperCase() + category.name.slice(1)}
                  </option>
                ))}
              </select>
            </div>
            
            <div className="transactions-table">
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Description</th>
                    <th>Category</th>
                    <th>Amount</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredExpenses.map(expense => (
                    <tr key={expense.id}>
                      <td>{new Date(expense.date).toLocaleDateString()}</td>
                      <td>{expense.description}</td>
                      <td>
                        <span className="category-tag">
                          <FontAwesomeIcon icon={getCategoryIcon(expense.category)} />
                          {expense.category}
                        </span>
                      </td>
                      <td className="expense">-${formatMoney(Number(expense.amount))}</td>
                      <td>
                        <button className="btn-icon">
                          <FontAwesomeIcon icon={faFileAlt} />
                        </button>
                        <button className="btn-icon">
                          <FontAwesomeIcon icon={faMinus} />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Expenses Budgets */}
        {activeMenu === 'expenses-budgets' && (
          <div className="expenses-budgets">
            <div className="budgets-grid">
              {budgets.map(budget => {
                const percentage = (budget.spent / budget.limit) * 100;
                const isOverBudget = percentage > 100;
                
                return (
                  <div key={budget.id} className="budget-card">
                    <div className="budget-header">
                      <div className="budget-icon">
                        <FontAwesomeIcon icon={getCategoryIcon(budget.category)} />
                      </div>
                      <h3>{budget.category.charAt(0).toUpperCase() + budget.category.slice(1)}</h3>
                    </div>
                    
                    <div className="budget-progress">
                      <div className="progress-info">
                        <span>${formatMoney(budget.spent)} of ${formatMoney(budget.limit)}</span>
                        <span>{Math.round(percentage)}%</span>
                      </div>
                      <div className="progress-bar">
                        <div 
                          className={`progress ${isOverBudget ? 'over' : ''}`}
                          style={{ width: `${Math.min(percentage, 100)}%` }}
                        ></div>
                      </div>
                    </div>
                    
                    <div className="budget-actions">
                      <button className="btn btn-small">Edit</button>
                      <button className="btn btn-small btn-secondary">History</button>
                    </div>
                  </div>
                );
              })}
            </div>
            
            <div className="budget-summary">
              <div className="summary-card">
                <h3>Total Budget</h3>
                <p>${formatMoney(budgets.reduce((sum, budget) => sum + budget.limit, 0))}</p>
              </div>
              
              <div className="summary-card">
                <h3>Total Spent</h3>
                <p>${formatMoney(budgets.reduce((sum, budget) => sum + budget.spent, 0))}</p>
              </div>
              
              <div className="summary-card">
                <h3>Remaining</h3>
                <p>${formatMoney(
                  budgets.reduce((sum, budget) => sum + budget.limit, 0) - 
                  budgets.reduce((sum, budget) => sum + budget.spent, 0)
                )}</p>
              </div>
            </div>
          </div>
        )}

        {/* Expenses Categories */}
        {activeMenu === 'expenses-categories' && (
          <div className="expenses-categories">
            <div className="categories-grid">
              {expenseCategories.map(category => (
                <div key={category.name} className="category-card">
                  <div className="category-icon">
                    <FontAwesomeIcon icon={category.icon} />
                  </div>
                  <h3>{category.name.charAt(0).toUpperCase() + category.name.slice(1)}</h3>
                  <p>${formatMoney(category.amount)}</p>
                  <div className="category-actions">
                    <button className="btn btn-small">View</button>
                    <button className="btn btn-small btn-secondary">Edit</button>
                  </div>
                </div>
              ))}
            </div>
            
            <div className="category-stats">
              <div className="chart-container">
                <h3>Category Distribution</h3>
                <Doughnut data={categoryData} />
              </div>
            </div>
          </div>
        )}

        {/* Other Menu Content */}
        {activeMenu === 'profile' && (
          <div className="menu-content">
            <h2>Profile Content</h2>
            <p>This section would display content for profile.</p>
          </div>
        )}
        
        {activeMenu === 'notifications' && (
          <div className="menu-content">
            <h2>Notifications Content</h2>
            <p>This section would display content for notifications.</p>
          </div>
        )}
      </div>

      {/* Chatbot */}
      <div className={`chatbot-container ${chatbotOpen ? 'open' : ''}`}>
        <div className="chatbot-header" onClick={() => setChatbotOpen(!chatbotOpen)}>
          <h3>BudgetBuddy Assistant</h3>
          <span>{chatbotOpen ? '▼' : '▲'}</span>
        </div>
        
        {chatbotOpen && (
          <div className="chatbot-content">
            <div className="chat-messages">
              {chatMessages.length > 0 ? (
                chatMessages.map((message, index) => (
                  <div key={index} className={`message ${message.sender}`}>
                    <p>{message.text}</p>
                    <small>{message.timestamp}</small>
                  </div>
                ))
              ) : (
                <div className="welcome-message">
                  <p>Hi! I'm your BudgetBuddy assistant. How can I help you today?</p>
                </div>
              )}
            </div>
            
            <form onSubmit={handleChatSubmit} className="chat-input">
              <input
                type="text"
                value={userMessage}
                onChange={(e) => setUserMessage(e.target.value)}
                placeholder="Ask me about your budget..."
              />
              <button type="submit">Send</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
