import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';

// Register Chart.js components
ChartJS.register(ArcElement, Tooltip, Legend);

const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [transactions, setTransactions] = useState([]);
    const [newTransaction, setNewTransaction] = useState({ type: 'income', amount: 0, description: '' });
    const [filterType, setFilterType] = useState('all'); // 'all', 'income', 'expense'
    const [filterDate, setFilterDate] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            const token = localStorage.getItem('access_token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                // Fetch user data
                const userResponse = await axios.get('http://localhost:8000/api/user/', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setUserData(userResponse.data);

                // Fetch transactions
                const transactionsResponse = await axios.get('http://localhost:8000/api/transactions/', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setTransactions(transactionsResponse.data);
            } catch (error) {
                console.error('Error fetching data:', error.response ? error.response.data : error.message);
                localStorage.removeItem('access_token');
                navigate('/login');
            }
        };

        fetchData();
    }, [navigate]);

    const handleAddTransaction = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('access_token');
        try {
            const response = await axios.post('http://localhost:8000/api/transactions/', newTransaction, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setTransactions([...transactions, response.data]);
            setNewTransaction({ type: 'income', amount: 0, description: '' });
        } catch (error) {
            console.error('Error adding transaction:', error.response ? error.response.data : error.message);
        }
    };

    // Calculate totals
    const income = transactions
        .filter((t) => t.type === 'income')
        .reduce((sum, t) => sum + parseFloat(t.amount), 0);

    const expenses = transactions
        .filter((t) => t.type === 'expense')
        .reduce((sum, t) => sum + parseFloat(t.amount), 0);

    const savings = income - expenses;

    // Filter transactions
    const filteredTransactions = transactions.filter((transaction) => {
        const matchesType = filterType === 'all' || transaction.type === filterType;
        const matchesDate = !filterDate || transaction.date.includes(filterDate);
        return matchesType && matchesDate;
    });

    // Prepare data for the chart
    const chartData = {
        labels: ['Income', 'Expenses'],
        datasets: [
            {
                label: 'Amount',
                data: [income, expenses],
                backgroundColor: ['#36A2EB', '#FF6384'],
                hoverBackgroundColor: ['#36A2EB', '#FF6384'],
            },
        ],
    };

    return (
        <div>
            <h2>Dashboard</h2>
            {userData ? (
                <div>
                    <p>Welcome, {userData.username}!</p>
                    <p>Email: {userData.email}</p>

                    <h3>Summary</h3>
                    <div>
                        <p>Total Income: ${income.toFixed(2)}</p>
                        <p>Total Expenses: ${expenses.toFixed(2)}</p>
                        <p>Savings: ${savings.toFixed(2)}</p>
                    </div>

                    <h3>Add Transaction</h3>
                    <form onSubmit={handleAddTransaction}>
                        <select
                            name="type"
                            value={newTransaction.type}
                            onChange={(e) => setNewTransaction({ ...newTransaction, type: e.target.value })}
                        >
                            <option value="income">Income</option>
                            <option value="expense">Expense</option>
                        </select>
                        <input
                            type="number"
                            name="amount"
                            placeholder="Amount"
                            value={newTransaction.amount}
                            onChange={(e) => setNewTransaction({ ...newTransaction, amount: e.target.value })}
                            required
                        />
                        <input
                            type="text"
                            name="description"
                            placeholder="Description"
                            value={newTransaction.description}
                            onChange={(e) => setNewTransaction({ ...newTransaction, description: e.target.value })}
                            required
                        />
                        <button type="submit">Add</button>
                    </form>

                    <h3>Filters</h3>
                    <div>
                        <label>
                            Filter by Type:
                            <select
                                value={filterType}
                                onChange={(e) => setFilterType(e.target.value)}
                            >
                                <option value="all">All</option>
                                <option value="income">Income</option>
                                <option value="expense">Expense</option>
                            </select>
                        </label>
                        <label>
                            Filter by Date:
                            <input
                                type="date"
                                value={filterDate}
                                onChange={(e) => setFilterDate(e.target.value)}
                            />
                        </label>
                    </div>

                    <h3>Transactions</h3>
                    <ul>
                        {filteredTransactions.map((transaction) => (
                            <li key={transaction.id}>
                                {transaction.type === 'income' ? '+' : '-'} ${transaction.amount} - {transaction.description}
                            </li>
                        ))}
                    </ul>

                    <h3>Spending Overview</h3>
                    <div style={{ width: '300px', margin: '0 auto' }}>
                        <Doughnut data={chartData} />
                    </div>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default Dashboard;
