import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes, faPaperPlane } from '@fortawesome/free-solid-svg-icons';
import './Chatbot.css';

const Chatbot = () => {
  const [chatbotOpen, setChatbotOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([
    { sender: 'bot', text: 'Hello! I\'m your BudgetBuddy assistant. How can I help you today?', timestamp: new Date().toLocaleTimeString() }
  ]);
  const [userMessage, setUserMessage] = useState('');

  const handleChatSubmit = async (e) => {
    e.preventDefault();
    if (!userMessage.trim()) return;
    
    const newUserMessage = {
      sender: 'user',
      text: userMessage,
      timestamp: new Date().toLocaleTimeString()
    };
    
    setChatMessages([...chatMessages, newUserMessage]);
    setUserMessage('');
    
    try {
      const response = await axios.post('http://localhost:8000/api/chatbot/', {
        message: userMessage,
        user_id: localStorage.getItem('user_id')
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
      });
      
      const botMessage = {
        sender: 'bot',
        text: response.data.response,
        timestamp: new Date().toLocaleTimeString()
      };
      setChatMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Chatbot error:', error);
      const botResponses = [
        "I'm having trouble connecting right now.",
        "Please try again later.",
        "I can't process your request at the moment."
      ];
      const botMessage = {
        sender: 'bot',
        text: botResponses[Math.floor(Math.random() * botResponses.length)],
        timestamp: new Date().toLocaleTimeString()
      };
      setChatMessages(prev => [...prev, botMessage]);
    }
  };

  return (
    <div className={`chatbot-container ${chatbotOpen ? 'open' : ''}`}>
      <div className="chatbot-header" onClick={() => setChatbotOpen(!chatbotOpen)}>
        <h3>BudgetBuddy Assistant</h3>
        <button 
          className="btn-icon" 
          onClick={(e) => {
            e.stopPropagation();
            setChatbotOpen(false);
          }}
        >
          <FontAwesomeIcon icon={faTimes} />
        </button>
      </div>
      
      {chatbotOpen && (
        <div className="chatbot-content">
          <div className="chat-messages">
            {chatMessages.map((message, index) => (
              <div key={index} className={`message ${message.sender}`}>
                <p>{message.text}</p>
                <small>{message.timestamp}</small>
              </div>
            ))}
          </div>
          
          <form onSubmit={handleChatSubmit} className="chat-input">
            <input
              type="text"
              value={userMessage}
              onChange={(e) => setUserMessage(e.target.value)}
              placeholder="Ask me about your budget..."
            />
            <button type="submit">
              <FontAwesomeIcon icon={faPaperPlane} />
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
