import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { 
  Chart as ChartJS, 
  ArcElement, 
  Tooltip, 
  Legend,
  CategoryScale,
  LinearScale,
  BarElement
} from 'chart.js';
import { Doughnut, Bar } from 'react-chartjs-2';
import './Dashboard.css';

// Register Chart.js components
ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement
);

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [comments, setComments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        // Fetch user data
        const userResponse = await axios.get('http://localhost:8000/api/user/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUserData(userResponse.data);

        // Fetch transactions
        const transactionsResponse = await axios.get('http://localhost:8000/api/transactions/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTransactions(transactionsResponse.data);

        // Fetch comments
        const commentsResponse = await axios.get('http://localhost:8000/api/comments/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setComments(commentsResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error.response ? error.response.data : error.message);
        localStorage.removeItem('access_token');
        navigate('/login');
      }
    };

    fetchData();
  }, [navigate]);

  // Calculate totals
  const income = transactions
    .filter((t) => t.type === 'income')
    .reduce((sum, t) => sum + parseFloat(t.amount), 0);

  const expenses = transactions
    .filter((t) => t.type === 'expense')
    .reduce((sum, t) => sum + parseFloat(t.amount), 0);

  const savings = income - expenses;

  // Prepare data for the charts
  const doughnutData = {
    labels: ['Income', 'Expenses'],
    datasets: [
      {
        label: 'Amount',
        data: [income, expenses],
        backgroundColor: ['#36A2EB', '#FF6384'],
        hoverBackgroundColor: ['#36A2EB', '#FF6384'],
      },
    ],
  };

  const barData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Income',
        data: [500, 600, 700, 800, 900, 1000],
        backgroundColor: '#36A2EB',
      },
      {
        label: 'Expenses',
        data: [300, 400, 500, 600, 700, 800],
        backgroundColor: '#FF6384',
      },
    ],
  };

  const barOptions = {
    responsive: true,
    scales: {
      x: {
        type: 'category',
      },
      y: {
        type: 'linear',
        beginAtZero: true
      }
    }
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <h1>BudgetBuddy</h1>
        <input type="text" placeholder="Search transactions..." />
      </div>

      {/* Overview Section */}
      <div className="overview-section">
        <div className="overview-card">
          <h3>Earnings</h3>
          <p>${income.toFixed(2)}</p>
        </div>
        <div className="overview-card">
          <h3>Expenses</h3>
          <p>${expenses.toFixed(2)}</p>
        </div>
        <div className="overview-card">
          <h3>Savings</h3>
          <p>${savings.toFixed(2)}</p>
        </div>
      </div>

      {/* Financial Overview */}
      <div className="financial-overview">
        <h2>Financial Overview</h2>
        <div className="chart-container">
          <div className="chart">
            <Doughnut data={doughnutData} />
          </div>
          <div className="chart">
            <Bar data={barData} options={barOptions} />
          </div>
        </div>
      </div>

      {/* Recent Transactions */}
      <div className="recent-transactions">
        <h2>Recent Transactions</h2>
        <ul>
          {transactions.slice(0, 5).map((transaction) => (
            <li key={transaction.id}>
              <span>{transaction.type === 'income' ? '+' : '-'} ${transaction.amount}</span>
              <span>{transaction.description}</span>
              <span>{new Date(transaction.date).toLocaleDateString()}</span>
            </li>
          ))}
        </ul>
      </div>

      {/* Comments Section */}
      <div className="comments-section">
        <h2>Comments</h2>
        {comments.length > 0 ? (
          comments.map((comment) => (
            <div key={comment.id} className="comment">
              <p>
                <strong>{comment.user?.username || 'Anonymous'}</strong> on 
                {comment.transaction?.description ? ` ${comment.transaction.description}` : ' a transaction'}
              </p>
              <p>{comment.text}</p>
              <small>{new Date(comment.date).toLocaleString()}</small>
            </div>
          ))
        ) : (
          <p>No comments yet</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
