import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import "./BudgetForm.css";

const BudgetForm = ({ isOpen, onClose, onBudgetAdded, expenseCategories }) => {
  const [newBudget, setNewBudget] = useState({
    category: expenseCategories.length > 0 ? expenseCategories[0] : "",
    limit: "",
    period: "monthly",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await onBudgetAdded({
        ...newBudget,
        limit: parseFloat(newBudget.limit),
      });
      onClose();
    } catch (error) {
      console.error("Error creating budget:", error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Create New Budget</h3>
          <button className="close-btn" onClick={onClose}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Category</label>
            <select value={newBudget.category} onChange={(e) => setNewBudget({ ...newBudget, category: e.target.value })} required>
              {expenseCategories.map((cat) => (
                <option key={cat} value={cat}>
                  {cat.charAt(0).toUpperCase() + cat.slice(1)}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Amount</label>
            <input type="number" value={newBudget.limit} onChange={(e) => setNewBudget({ ...newBudget, limit: e.target.value })} required />
          </div>

          <div className="form-group">
            <label>Period</label>
            <select value={newBudget.period} onChange={(e) => setNewBudget({ ...newBudget, period: e.target.value })} required>
              <option value="weekly">Weekly</option>
              <option value="monthly">Monthly</option>
              <option value="yearly">Yearly</option>
            </select>
          </div>

          <div className="form-actions">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary">
              Create Budget
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BudgetForm;
